import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L from 'leaflet';

// Modal popup styles
const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    height: '100vh',
    width: '100vw',
    backgroundColor: 'rgba(0,0,0,0.4)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000
  },
  modal: {
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '12px',
    width: '80%',
    maxWidth: '500px',
    boxShadow: '0 0 20px rgba(0,0,0,0.3)'
  },
  closeButton: {
    marginTop: '15px',
    padding: '8px 12px',
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer'
  }
};

// Custom icons
const blueDotIcon = new L.DivIcon({
  html: '<div style="width: 16px; height: 16px; background: rgba(0,123,255,0.7); border-radius: 50%; box-shadow: 0 0 12px rgba(0,123,255,0.5);"></div>',
  className: ''
});

const greenIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-green.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

const MapComponent = () => {
  const [locations, setLocations] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [sortedLocations, setSortedLocations] = useState([]);
  const [showList, setShowList] = useState(false);

  // Get distance using haversine formula
  const getDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1 * Math.PI / 180) *
      Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  useEffect(() => {
    axios.get('http://localhost:5000/api/locations')
      .then((res) => setLocations(res.data));

    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation({ lat: latitude, lng: longitude });

        // Update distances in backend
        fetch('http://localhost:5000/update-distances', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ userLat: latitude, userLng: longitude })
        })
          .then(res => res.json())
          .then(data => console.log("‚úÖ Distances updated", data))
          .catch(err => console.error("‚ùå Failed to update distances:", err));
      }, (err) => {
        console.error("Geolocation error:", err);
      });
    }
  }, []);

  const findNearest = () => {
    if (!userLocation || locations.length === 0) return;

    const sorted = [...locations].map((loc) => {
      const dist = getDistance(
        userLocation.lat,
        userLocation.lng,
        Number(loc.latitude),
        Number(loc.longitude)
      );
      return { ...loc, distance: dist };
    });

    sorted.sort((a, b) => a.distance - b.distance);
    setSortedLocations(sorted);
    setShowList(true);
  };

  return (
    <>
      <button onClick={findNearest} style={{ margin: '10px', padding: '10px 20px', fontWeight: 'bold' }}>
        Find Nearest
      </button>

      <MapContainer
        center={[20.5937, 78.9629]}
        zoom={5}
        style={{
          height: "600px",
          width: "100%",
          maxWidth: "800px",
          margin: "0 auto",
          border: "2px solid #ccc",
          borderRadius: "8px"
        }}
        maxBounds={[[85, -180], [-85, 180]]}
        maxBoundsViscosity={1.0}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          noWrap={true}
          bounds={[[85, -180], [-85, 180]]}
        />

        {locations.map(loc => {
          const distance = userLocation
            ? getDistance(userLocation.lat, userLocation.lng, Number(loc.latitude), Number(loc.longitude)).toFixed(2)
            : null;

          return (
            <Marker
              key={loc._id}
              position={[loc.latitude, loc.longitude]}
              icon={greenIcon}
            >
              <Popup>
                <strong>{loc.name}</strong><br />
                {distance ? `Distance: ${distance} km` : 'Distance: Unknown'}<br /><br />
                <a href={`http://localhost:5000/${loc._id}`} target="_blank" rel="noopener noreferrer">
                  View more details
                </a>
              </Popup>
            </Marker>
          );
        })}

        {userLocation && (
          <Marker position={[userLocation.lat, userLocation.lng]} icon={blueDotIcon}>
            <Popup>Your Location</Popup>
          </Marker>
        )}
      </MapContainer>

      {/* Modal Popup for nearest list */}
      {showList && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <h3>üìç Nearest Locations</h3>
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {sortedLocations.map((loc, idx) => (
                <li key={loc._id} style={{ marginBottom: '10px' }}>
                  <a
                    href={`http://localhost:5000/${loc._id}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                      textDecoration: 'none',
                      color: '#007bff',
                      fontWeight: 'bold'
                    }}
                  >
                    {idx + 1}. {loc.name} - {loc.distance.toFixed(2)} km
                  </a>
                </li>
              ))}
            </ul>
            <button onClick={() => setShowList(false)} style={modalStyles.closeButton}>Close</button>
          </div>
        </div>
      )}
    </>
  );
};

export default MapComponent;
