import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L from 'leaflet';

// Blue user location dot
const blueDotIcon = new L.DivIcon({
  html: '<div style="width: 16px; height: 16px; background: rgba(0,123,255,0.7); border-radius: 50%; box-shadow: 0 0 12px rgba(0,123,255,0.5);"></div>',
  className: ''
});

// Green pin icon for locations
const greenIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-green.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

// Default marker config
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

const MapComponent = () => {
  const [locations, setLocations] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [nearest, setNearest] = useState(null);

  // 1. Fetch locations and user position
  useEffect(() => {
    axios.get('http://localhost:5000/api/locations')
      .then((res) => setLocations(res.data));

    navigator.geolocation.getCurrentPosition((pos) => {
      setUserLocation({
        lat: pos.coords.latitude,
        lng: pos.coords.longitude,
      });
    });
  }, []);

  // 2. After mount, send location to backend to update distances
  useEffect(() => {
    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const userLat = position.coords.latitude;
          const userLng = position.coords.longitude;

          fetch('http://localhost:5000/update-distances', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ userLat, userLng }),
          })
            .then((res) => res.json())
            .then((data) => console.log('Distances updated', data))
            .catch((err) => console.error('Error updating distances:', err));
        },
        (error) => {
          console.error('Geolocation error:', error);
        }
      );
    } else {
      console.error('Geolocation not supported');
    }
  }, []);

  // 3. Haversine distance calculator
  const getDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  // 4. Find nearest location to user
  const findNearest = () => {
    if (!userLocation || locations.length === 0) return;

    let nearestLoc = null;
    let minDistance = Infinity;

    locations.forEach(loc => {
      const dist = getDistance(userLocation.lat, userLocation.lng, loc.latitude, loc.longitude);
      if (dist < minDistance) {
        minDistance = dist;
        nearestLoc = loc;
      }
    });

    setNearest(nearestLoc);
    alert(`Nearest Location: ${nearestLoc.name} (${minDistance.toFixed(2)} km)`);
  };

  return (
    <>
      <button onClick={findNearest} style={{ margin: '10px', padding: '8px' }}>
        Find Nearest
      </button>

      <MapContainer
        center={[20.5937, 78.9629]}
        zoom={5}
        style={{
          height: "600px",
          width: "100%",
          maxWidth: "800px",
          margin: "0 auto",
          border: "2px solid #ccc",
          borderRadius: "8px"
        }}
        maxBounds={[[85, -180], [-85, 180]]}
        maxBoundsViscosity={1.0}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          noWrap={true}
          bounds={[[85, -180], [-85, 180]]}
        />

        {/* All pin locations */}
        {locations.map(loc => {
          const distance = userLocation
            ? getDistance(userLocation.lat, userLocation.lng, loc.latitude, loc.longitude).toFixed(2)
            : null;

          return (
            <Marker
              key={loc._id}
              position={[loc.latitude, loc.longitude]}
              icon={greenIcon}
            >
              <Popup>
                <strong>{loc.name}</strong><br />
                {distance ? `Distance: ${distance} km` : 'Distance: Unknown'}<br /><br />
                <a href={`http://localhost:5000/${loc._id}`} target="_blank" rel="noopener noreferrer">
    View more details
  </a>
              </Popup>
            </Marker>
          );
        })}

        {/* User location marker */}
        {userLocation && (
          <Marker position={[userLocation.lat, userLocation.lng]} icon={blueDotIcon}>
            <Popup>Your Location</Popup>
          </Marker>
        )}

        {/* Nearest location marker */}
        {nearest && (
          <Marker position={[nearest.latitude, nearest.longitude]}>
            <Popup>Nearest: {nearest.name}</Popup>
          </Marker>
        )}
      </MapContainer>
    </>
  );
};

export default MapComponent;
