import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L from 'leaflet';

const greenIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-green.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const blueDotIcon = new L.DivIcon({
  html: '<div style="width: 16px; height: 16px; background: rgba(0,123,255,0.7); border-radius: 50%; box-shadow: 0 0 12px rgba(0,123,255,0.5);"></div>',
  className: ''
});

const MapComponent = () => {
  const [locations, setLocations] = useState([]);
  const [userLocation, setUserLocation] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/locations').then((res) => setLocations(res.data));

    navigator.geolocation.getCurrentPosition((pos) => {
      const userLat = pos.coords.latitude;
      const userLng = pos.coords.longitude;
      setUserLocation({ lat: userLat, lng: userLng });

      fetch('http://localhost:5000/update-travel-times', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ userLat, userLng }),
      })
        .then((res) => res.json())
        .then((data) => console.log('Travel times updated', data))
        .catch((err) => console.error('Failed to update travel times', err));
    });
  }, []);

  return (
    <>
      <MapContainer
        center={[20.5937, 78.9629]}
        zoom={5}
        style={{ height: "600px", width: "100%", maxWidth: "800px", margin: "0 auto", borderRadius: "10px" }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {locations.map(loc => (
          <Marker
            key={loc._id}
            position={[loc.latitude, loc.longitude]}
            icon={greenIcon}
          >
            <Popup>
              <strong>{loc.name}</strong><br />
              {loc.distance ? `Distance: ${loc.distance.toFixed(2)} km` : 'Distance unknown'}<br />
              {loc.travelTime ? `Time: ${loc.travelTime}` : 'Time unknown'}<br />
              <form method="GET" action={`http://localhost:5000/${loc._id}`}>
                <button type="submit">More Info</button>
              </form>
            </Popup>
          </Marker>
        ))}

        {userLocation && (
          <Marker position={[userLocation.lat, userLocation.lng]} icon={blueDotIcon}>
            <Popup>Your Location</Popup>
          </Marker>
        )}
      </MapContainer>
    </>
  );
};

export default MapComponent;
