import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L from 'leaflet';

// -------------------- STYLES --------------------
const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    height: '100vh',
    width: '100vw',
    backgroundColor: 'rgba(0,0,0,0.4)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 1000
  },
  modal: {
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '12px',
    width: '80%',
    maxWidth: '500px',
    boxShadow: '0 0 20px rgba(0,0,0,0.3)'
  },
  closeButton: {
    marginTop: '15px',
    padding: '8px 12px',
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    borderRadius: '6px',
    cursor: 'pointer'
  }
};

// -------------------- ICONS --------------------
const blueDotIcon = new L.DivIcon({
  html: '<div style="width: 16px; height: 16px; background: rgba(0,123,255,0.7); border-radius: 50%; box-shadow: 0 0 12px rgba(0,123,255,0.5);"></div>',
  className: ''
});

const greenIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-green.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

// -------------------- COMPONENT --------------------
const MapComponent = () => {
  const [locations, setLocations] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [sortedLocations, setSortedLocations] = useState([]);
  const [showList, setShowList] = useState(false);
  const [start, setStart] = useState(null);
  const [end, setEnd] = useState(null);
  const [routePoints, setRoutePoints] = useState([]);
  const [filtered, setFiltered] = useState([]);



  const getDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a =
      Math.sin(dLat / 2) ** 2 +
      Math.cos(lat1 * Math.PI / 180) *
      Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon / 2) ** 2;
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  useEffect(() => {
    axios.get('http://localhost:5000/api/locations')
      .then((res) => setLocations(res.data));

    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition((position) => {
        const { latitude, longitude } = position.coords;
        setUserLocation({ lat: latitude, lng: longitude });
        const initAutocomplete = (id, setter) => {
  const input = document.getElementById(id);
  const autocomplete = new window.google.maps.places.Autocomplete(input);
  autocomplete.addListener('place_changed', () => {
    const place = autocomplete.getPlace();
    if (place.geometry) {
      setter({
        lat: place.geometry.location.lat(),
        lng: place.geometry.location.lng(),
        name: place.formatted_address
      });
    }
  });
};

initAutocomplete('start', setStart);
initAutocomplete('end', setEnd);


        fetch('http://localhost:5000/update-distances', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ userLat: latitude, userLng: longitude })
        })
          .then(res => res.json())
          .then(data => console.log("✅ Distances updated", data))
          .catch(err => console.error("❌ Failed to update distances:", err));
      }, (err) => {
        console.error("Geolocation error:", err);
      });
    }
  }, []);

  const findNearest = () => {
    if (!userLocation || locations.length === 0) return;


   const level = parseInt(prompt("Enter your current battery percentage:"), 10);
    if (isNaN(level) || level < 0 || level > 100) {
      alert("Please enter a valid percentage between 0 and 100.");
      return;
    }



    const updated = locations.map((loc) => {
      const actualDist = getDistance(
        userLocation.lat,
        userLocation.lng,
        Number(loc.latitude),
        Number(loc.longitude)
      );
      const isRenewable = loc.sourceType === 'renewable';
      const adjustedDist = isRenewable ? Math.max(actualDist - 5, 0) : actualDist;
      return {
        ...loc,
        actualDistance: actualDist,
        adjustedDistance: adjustedDist
      };
    });
    if(level>20){
    updated.sort((a, b) => a.adjustedDistance - b.adjustedDistance);}
    else{
      updated.sort((a, b) => a.actualDistance - b.actualDistance);
    }
    setSortedLocations(updated);
    setShowList(true);
  };
  const handleRoute = async () => {
  if (!start || !end) return;

  const apiKey = 'AIzaSyCvztkSEiC4GxHay6L5qsOep7cZ-Ndut4g'; // Replace safely
  const url = `https://maps.googleapis.com/maps/api/directions/json?origin=${start.lat},${start.lng}&destination=${end.lat},${end.lng}&key=${apiKey}`;

  try {
    const res = await axios.get(url);
    const encoded = res.data.routes[0].overview_polyline.points;
    const path = polyline.decode(encoded).map(([lat, lng]) => ({ lat, lng }));
    setRoutePoints(path);

    // Filter locations within 5km of route
    const line = turf.lineString(path.map(p => [p.lng, p.lat]));
    const nearby = locations.filter(loc => {
      const pt = turf.point([loc.longitude, loc.latitude]);
      const dist = turf.pointToLineDistance(pt, line, { units: 'kilometers' });
      return dist <= 5;
    });

    setFiltered(nearby);
  } catch (err) {
    console.error('Directions API error:', err);
  }
};


  return (
    <>
      <button onClick={findNearest} style={{ margin: '10px', padding: '10px 20px', fontWeight: 'bold' }}>
        Find Nearest
      </button>
      <div style={{ margin: '10px' }}>
  <input id="start" placeholder="Start location" style={{ marginRight: 10 }} />
  <input id="end" placeholder="End location" style={{ marginRight: 10 }} />
  <button onClick={handleRoute}>Show Route with Nearby Stations</button>
</div>


      <MapContainer
        center={[20.5937, 78.9629]}
        zoom={5}
        style={{
          height: "600px",
          width: "100%",
          maxWidth: "800px",
          margin: "0 auto",
          border: "2px solid #ccc",
          borderRadius: "8px"
        }}
        maxBounds={[[85, -180], [-85, 180]]}
        maxBoundsViscosity={1.0}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          noWrap={true}
          bounds={[[85, -180], [-85, 180]]}
        />
        {routePoints.length > 0 && (
  <Polyline positions={routePoints.map(p => [p.lat, p.lng])} color="blue" />
)}

{filtered.map(loc => (
  <Marker key={loc._id} position={[loc.latitude, loc.longitude]} icon={greenIcon}>
    <Popup>
      <b>{loc.name}</b><br />
      {loc.sourceType}<br />
      <a href={`http://localhost:5000/${loc._id}`} target="_blank">View</a>
    </Popup>
  </Marker>
))}


        {locations.map(loc => {
          const distance = userLocation
            ? getDistance(userLocation.lat, userLocation.lng, Number(loc.latitude), Number(loc.longitude)).toFixed(2)
            : null;

          return (
            <Marker
              key={loc._id}
              position={[loc.latitude, loc.longitude]}
              icon={greenIcon}
            >
              <Popup>
                <strong>{loc.name}</strong><br />
                {distance ? `Distance: ${distance} km` : 'Distance: Unknown'}<br />
                <a href={`http://localhost:5000/${loc._id}`} target="_blank" rel="noopener noreferrer">
                  View more details
                </a>
              </Popup>
            </Marker>
          );
        })}

        {userLocation && (
          <Marker position={[userLocation.lat, userLocation.lng]} icon={blueDotIcon}>
            <Popup>Your Location</Popup>
          </Marker>
        )}
      </MapContainer>

      {/* ------------------- MODAL ------------------- */}
      {/* Nearest List Modal */}
{showList && (
  <div style={{
    position: 'fixed',
    top: 0,
    left: 0,
    height: '100vh',
    width: '100vw',
    backgroundColor: 'rgba(0,0,0,0.5)',
    zIndex: 1000,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  }}>
    <div style={{
      backgroundColor: 'white',
      padding: '20px',
      borderRadius: '10px',
      width: '80%',
      maxWidth: '500px',
      maxHeight: '80vh',        
      overflowY: 'auto',        
      boxShadow: '0 4px 20px rgba(0,0,0,0.3)'
    }}>
      <h2>Nearest Charging Stations</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {sortedLocations.slice(0, 10).map((loc, index) => (
          <li key={loc._id} style={{ marginBottom: '15px', borderBottom: '1px solid #ddd', paddingBottom: '10px' }}>
            <strong>{index + 1}. {loc.name}</strong><br />
            Source: {loc.sourceType}<br />
            Distance: {loc.actualDistance.toFixed(2)} km<br />
            <form method="GET" action={`http://localhost:5000/${loc._id}`}>
              <button type="submit" style={{ marginTop: '5px' }}>View Details</button>
            </form>
          </li>
        ))}
      </ul>

      <button
        onClick={() => setShowList(false)}
        style={{
          marginTop: '20px',
          padding: '8px 12px',
          backgroundColor: '#dc3545',
          color: 'white',
          border: 'none',
          borderRadius: '6px',
          cursor: 'pointer'
        }}
      >
        Close
      </button>
    </div>
  </div>
)}

    </>
  );
};

export default MapComponent;
