export default function ChargerList({ chargers, onStatusChange }) {
  const handlePlugOut = async (chargerId) => {
    const res = await fetch(`/api/chargers/${chargerId}/plugout`, { method: "POST" });
    const updated = await res.json();
    onStatusChange(updated); // Notify parent to update count
  };

  return (
    <div>
      {chargers.map((charger, index) => (
        <div key={charger._id}>
          <li>
            <b>charger {index + 1}</b> - {charger.type} ({charger.power} kW, Status: {charger.status})
          </li>
          {/* your existing charging time + plug out button */}
          <button onClick={() => handlePlugOut(charger._id)}>ðŸ”Œ Plug Out</button>
        </div>
      ))}
    </div>
  );
}
