import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import MapComponent from './components/MapComponent';
import AddLocationForm from './components/AddLocationForm';
import LocationPage from './pages/LocationPage';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import Dashboard from './components/Dashboard'; // Your protected page
import { AuthProvider, AuthContext } from './context/AuthContext';
import { useContext } from 'react';

// ‚úÖ Wrapper to protect routes
function ProtectedRoute({ children }) {
  const { user } = useContext(AuthContext);
  return user ? children : <Navigate to="/login" />;
}

const App = () => {
  return (
    <AuthProvider>
      <Router>
        <Routes>

          {/* ‚úÖ Auth Routes */}
          <Route path="/login" element={<LoginForm />} />
          <Route path="/register" element={<RegisterForm />} />

          {/* ‚úÖ Protected Route Example */}
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <Dashboard />
              </ProtectedRoute>
            }
          />

          {/* üåç Home Page - Map and Add Location Form */}
          <Route
            path="/"
            element={
              <div>
                <h2 style={{ textAlign: 'center' }}>Find Your Charging Station</h2>
                <AddLocationForm />
                <MapComponent />
              </div>
            }
          />

          {/* üìç Location Detail Page */}
          <Route path="/location/:id" element={<LocationPage />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
};

export default App;
